import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles.css';

const AgendaCliente = () => {
    const [selectedDate, setSelectedDate] = useState('');
    const [availableTimes, setAvailableTimes] = useState([]);

    useEffect(() => {
        if (selectedDate) {
            axios.get(`http://localhost:5000/api/horarios/${selectedDate}`)
                .then(response => {
                    setAvailableTimes(response.data);
                })
                .catch(error => {
                    console.error("Houve um erro ao buscar os dados!", error);
                });
        }
    }, [selectedDate]);

    const handleReserve = (id) => {
        axios.post(`http://localhost:5000/api/horarios/reservar/${id}`)
            .then(() => {
                setAvailableTimes(availableTimes.map(time =>
                    time._id === id ? { ...time, reservado: true } : time
                ));
            })
            .catch(error => {
                console.error("Houve um erro ao reservar o horário!", error);
            });
    }

    return (
        <div className="container">
            <h1>Andreza Nail Designer</h1>
            <div className="agenda">
                <h2>Reservar Horário</h2>
                <label htmlFor="datePicker">Selecione uma Data:</label>
                <input 
                    type="date" 
                    id="datePicker" 
                    onChange={(e) => setSelectedDate(e.target.value)} 
                />
                <div id="availableTimes">
                    {availableTimes.length > 0 ? (
                        availableTimes.map(time => (
                            <div key={time._id} className="time">
                                <span>{time.time}</span>
                                <button 
                                    disabled={time.reservado} 
                                    onClick={() => handleReserve(time._id)}
                                >
                                    {time.reservado ? 'Este horário está reservado' : 'Reservar'}
                                </button>
                            </div>
                        ))
                    ) : (
                        <p>Não temos horários disponíveis para hoje.</p>
                    )}
                </div>
            </div>
        </div>
    );
}

export default AgendaCliente;
