import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles.css';

const AgendaCliente = () => {
    const [dataSelecionada, setDataSelecionada] = useState('');
    const [horariosDisponiveis, setHorariosDisponiveis] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        if (dataSelecionada) {
            setLoading(true);
            axios.get(`http://localhost:5000/api/horarios/${dataSelecionada}`)
                .then(response => {
                    setHorariosDisponiveis(response.data);
                    setLoading(false);
                    setError(null);
                })
                .catch(error => {
                    setError("Houve um erro ao buscar os dados!");
                    setLoading(false);
                    console.error("Houve um erro ao buscar os dados!", error);
                });
        }
    }, [dataSelecionada]);

    const handleReserve = (id) => {
        axios.post(`http://localhost:5000/api/horarios/reservar/${id}`)
            .then(() => {
                setHorariosDisponiveis(horariosDisponiveis.map(horario =>
                    horario._id === id ? { ...horario, reservado: true } : horario
                ));
                setError(null);
            })
            .catch(error => {
                setError("Houve um erro ao reservar o horário!");
                console.error("Houve um erro ao reservar o horário!", error);
            });
    }

    return (
        <div className="container">
      <h1 className="text-center mt-5">Andreza Nail Designer</h1>
      <form className="mt-5">
        <div className="form-group">
          <label htmlFor="date">Selecione uma Data:</label>
          <input
            type="date"
            className="form-control"
            id="date"
            name="date"
            value={selectedDate}
            onChange={handleDateChange}
            required
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Reservar Horário
        </button>
      </form>
      <p className="mt-3 text-center">Não temos horários disponíveis para hoje.</p>
    </div>
  );
}

export default AgendaCliente;
